import faker from 'faker';
import _ from 'lodash';
import React, { Component } from 'react';
import logo from './logo.svg';
import Amplify, { API, graphqlOperation, Auth } from 'aws-amplify';
import './App.css';
import awsmobile from './aws-exports';
import { render } from "react-dom";
import { Bootstrap } from 'a-theme-react';

import { AmplifyAuthenticator, withAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';

import { Button, Input, Form, Grid, Header, Image, Message, Segment, Dropdown } from 'semantic-ui-react'

const addressDefinitions = faker.definitions.address
const stateOptions = _.map(addressDefinitions.state, (state, index) => ({
  key: addressDefinitions.state_abbr[index],
  text: state,
  value: addressDefinitions.state_abbr[index],
}))


class App extends Component {

  state = { apiResponse: null };

  async loadEvents() {
   const path = "/iotevents/list"; // you can specify the path
   /*let myInit = {
     headers: {   
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Headers": "Content-Type,X-Amz-Date,X-Amz-Security-Token,Authorization,X-Api-Key,X-Requested-With,Accept,Access-Control-Allow-Methods,Access-Control-Allow-Origin,Access-Control-Allow-Headers"
      }
    }*/
    try{
      const apiResponse = await API.get("api75996003" , path);
      console.log('response:' + apiResponse);

      this.setState({ apiResponse });
    }catch(err){
      console.log("error calling api: " + err);
    }
  }

  models = [];

  async addEventModel() {
   const path = "/items/iotevents"; // you can specify the path
    const apiResponse = await API.post("api75996003" , path);
    console.log('response:' + apiResponse);
    this.setState({ apiResponse });
  }

  render() {
    return (
      <AmplifyAuthenticator theme={Bootstrap}>
        <div className="App" style={styles.container}>
        <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
          <Grid.Column style={{ maxWidth: 450 }}>
            <Header as='h2' color='teal' textAlign='center'>
              IoT Events - Demo
            </Header>
            <Segment stacked>
              <Segment basic padded>
                <Segment.Inline >
                  <Dropdown placeholder='Existing Event Models' search selection onOpen={this.loadEvents.bind(this)} options={this.models}  />
                  <Button secondary>Remove</Button>
                </Segment.Inline>
              </Segment>
              <Segment.Group basic>
                <Segment><Input placeholder="Event Model Name..." /></Segment>
                <Segment><Input placeholder="Alarm Timeout(s)..." /></Segment>
                <Segment><Input placeholder="Input Topic..." /></Segment>
              </Segment.Group>
              <Segment.Inline >
                <Button primary>Add</Button>
              </Segment.Inline>
            </Segment>
            <AmplifySignOut />
          </Grid.Column>
        </Grid>
      </div>
    </AmplifyAuthenticator>
    )
  }
}

const styles = {
  container: { width: 400, margin: '0 auto', display: 'flex', flex: 1, flexDirection: 'column', justifyContent: 'center', padding: 20 },
  todo: {  marginBottom: 15 },
  input: { border: 'none', backgroundColor: '#ddd', marginBottom: 10, padding: 8, fontSize: 18 },
  todoName: { fontSize: 20, fontWeight: 'bold' },
  todoDescription: { marginBottom: 0 },
  button: { backgroundColor: 'black', color: 'white', outline: 'none', fontSize: 18, padding: '12px 0px' }
}

export default App
//export default withAuthenticator(App, {  includeGreetings: true})
