import _ from 'lodash';
import React, { Component } from 'react';
import logo from './logo.svg';
import Amplify, { API, graphqlOperation, Auth } from 'aws-amplify';
import './App.css';
import awsmobile from './aws-exports';
import { render } from "react-dom";
import { Bootstrap } from 'a-theme-react';

import { AmplifyAuthenticator, withAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';

import { Button, Input, Icon, Form, Grid, Header, Image, Message, Segment, Dropdown } from 'semantic-ui-react'


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isFetching: true,
      isFetchingDetectors: true,
      value: [],
      options: [],
      template: [],
      modelname: "",
      topic: "",
      timeout: 0,
      viewmodelname: "",
      modelUniqueId: "",
      detectorinstances: []
    }
  }


  handleChange = (e, { value }) => {
  this.setState({value: value })
  }

  handleTemplateChange = (e, { value }) => {
  this.setState({ template: value })
  }

  handleTopicChange = (e, { value }) => {
  this.setState({ topic: value })
  }

  handleModelNameChange = (e, { value }) => {
  this.setState({ modelname: value})
  }

  handleTimeoutChange = (e, { value }) => {
  this.setState({ timeout: value })
  }

  handleEventModelChange = (e, { value }) => {
    this.setState({ viewmodelname: value})
    //get alarmids
    this.loadDetectorInstances();
  }

  handleEventModelIdChange = (e, { value }) => {
  this.setState({ modelUniqueId: value})
  }

  async loadEvents() {
   const path = "/iotevents/list"; // you can specify the path
    try{
      const apiResponse = await API.get("api75996003" , path);
      this.setState({ options: apiResponse.options });
      this.setState({isFetching: false});
    }catch(err){
      console.log("error calling api: " + err);
    }
  }

  async loadDetectorInstances() {
    this.setState({isFetchingDetectors: true});
   const path = "/iotevents/listdetectors"; // you can specify the path
   console.log(this.state.topic)
   const params = {
      "modelname": this.state.viewmodelname
   }
   console.log(params);
   const myInit = {
    "body": params
    };
    const apiResponse = await API.get("api75996003" , path, myInit);
    this.setState({ options: apiResponse.options });
    this.setState({isFetchingDetectors: false});

    //Load detectors
  }

  handleClickAdd = async ()  => {
    console.log("Adding now...")
    const path = "/iotevents/add"; // you can specify the path
   console.log(this.state.topic)
   const params = {
      "template": this.state.template,
      "topic":  this.state.topic,
      "timeout":  this.state.timeout,
      "modelname":  this.state.modelname
   }
   console.log(params);
   const myInit = {
    "body": params
    };
    console.log("Calling API to add")
    const apiResponse = await API.put("api75996003" , path, myInit);
    console.log('response:' + apiResponse);
  }
  componentDidMount() {
       this.loadEvents();
  }

  render() {
    const {isFetching, isFetchingDetectors, options, value, viewmodelname, modelUniqueId, template, topic, modelname, timeout, detectorinstances } = this.state

    const templateOptions = [
      {
        key: 'Simple Alarm',
        text: 'Simple Alarm',
        value: 'Simple Alarm'
      },
      {
        key: 'HVAC Temperature Control',
        text: 'HVAC Temperature Control',
        value: 'HVAC Temperature Control',
      }
    ]
    return (
      <AmplifyAuthenticator>
        <div className="App">
        <Grid padded centered>
          <Grid.Column width='10'>
          <Header as='h1' icon textAlign='center'>
            <Image src='./iotevents.png'  />
            <Header.Content>IoT Events - Demo</Header.Content>
          </Header>
              <Segment>
                <Header as='h3' textAlign='left'>
                  <Icon name='settings' />
                  <Header.Content>
                    Create Event Model
                    <Header.Subheader>Create a new IoT Event model</Header.Subheader>
                  </Header.Content>
                </Header>
                <Segment basic>
                  <Segment.Group>
                    <Segment.Group>
                    <Dropdown 
                      placeholder='Select Event Model template' 
                      fluid
                      options={templateOptions}
                      onChange={this.handleTemplateChange}
                      />
                    </Segment.Group>
                    <Segment><Input fluid onChange={this.handleModelNameChange} placeholder="Event Model Name..." /></Segment>
                    <Segment><Input fluid onChange={this.handleTimeoutChange} placeholder="Alarm Timeout(s)..." /></Segment>
                    <Segment><Input fluid onChange={this.handleTopicChange} placeholder="Input Topic..." /></Segment>
                    <Segment><Button primary onClick={this.handleClickAdd}>Add Model</Button></Segment>
                  </Segment.Group>
                </Segment>
              </Segment>
              <Segment>
                <Header as='h3' textAlign='left'>
                  <Icon name='settings' />
                  <Header.Content>
                    Monitor Event Model
                    <Header.Subheader>Select an IoT Event to monitor it's state</Header.Subheader>
                  </Header.Content>
                </Header>
                <Segment basic>
                  <Segment.Group>
                    <Segment.Group>
                      <Dropdown fluid placeholder='Select an Event Model' 
                        disabled={isFetching}
                        loading={isFetching}
                        options={options}
                        defaultValue={viewmodelname}
                        onChange={this.handleEventModelChange}
                        />
                    </Segment.Group>
                    <Segment.Group>
                      <Dropdown fluid placeholder='Select model id' 
                        disabled={isFetchingDetectors}
                        loading={isFetchingDetectors}
                        options={detectorinstances}
                        defaultValue={modelUniqueId}
                        onChange={this.handleEventModelIdChange}
                        />
                    </Segment.Group>
                    <Segment><Input fluid onChange={this.handleModelNameChange}  /></Segment>
                  </Segment.Group>
                </Segment>
              </Segment>
              <Segment>
                <Header as='h3' textAlign='left'>
                  <Icon name='settings' />
                  <Header.Content>
                    Event Models
                    <Header.Subheader>Remove existing IoT Event models or inputs</Header.Subheader>
                  </Header.Content>
                </Header>
                <Segment basic>
                  <Segment.Group horizontal>
                  <Dropdown fluid placeholder='Existing Event Models' 
                        disabled={isFetching}
                        loading={isFetching}
                        options={options}
                        defaultValue={value}
                        onChange={this.handleChange}
                        />
                    <Button secondary>Remove Model</Button>
                  </Segment.Group>
                </Segment>
              </Segment>
            <AmplifySignOut />
          </Grid.Column>
        </Grid>
      </div>
    </AmplifyAuthenticator>
    )
  }
}

const styles = {
  container: { width: 500, margin: '0 auto', display: 'flex', flex: 1, flexDirection: 'column', justifyContent: 'center', padding: 20 },
  todo: {  marginBottom: 15 },
  input: { border: 'none', backgroundColor: '#ddd', marginBottom: 10, padding: 8, fontSize: 18 },
  button: { backgroundColor: 'black', color: 'white', outline: 'none', fontSize: 18, padding: '12px 0px' }
}

export default App
//export default withAuthenticator(App, {  includeGreetings: true})
