import faker from 'faker';
import _ from 'lodash';
import React from 'react';
import logo from './logo.svg';
import Amplify from 'aws-amplify';
import './App.css';
import { Bootstrap } from 'a-theme-react';

import { AmplifyAuthenticator, withAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';

import { Button, Form, Grid, Header, Image, Message, Segment, Dropdown } from 'semantic-ui-react'

const addressDefinitions = faker.definitions.address
const stateOptions = _.map(addressDefinitions.state, (state, index) => ({
  key: addressDefinitions.state_abbr[index],
  text: state,
  value: addressDefinitions.state_abbr[index],
}))

//    <Dropdown placeholder='State' search selection options={stateOptions} />

const App = () => {
  return (
    <AmplifyAuthenticator theme={Bootstrap}>
      <div className="App" style={styles.container}>
      <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 450 }}>
          <Header as='h2' color='teal' textAlign='center'>
            IoT Events - Demo
          </Header>
          <Segment stacked>
            <Dropdown placeholder='State' search selection options={stateOptions} />
            <Segment horizontal basic>
              <Segment basic><Button primary>Add</Button></Segment>
              <Segment basic><Button secondary>Remove</Button></Segment>
            </Segment>
          </Segment>
          <AmplifySignOut />
        </Grid.Column>
      </Grid>
    </div>
  </AmplifyAuthenticator>
  )
}

const styles = {
  container: { width: 400, margin: '0 auto', display: 'flex', flex: 1, flexDirection: 'column', justifyContent: 'center', padding: 20 },
  todo: {  marginBottom: 15 },
  input: { border: 'none', backgroundColor: '#ddd', marginBottom: 10, padding: 8, fontSize: 18 },
  todoName: { fontSize: 20, fontWeight: 'bold' },
  todoDescription: { marginBottom: 0 },
  button: { backgroundColor: 'black', color: 'white', outline: 'none', fontSize: 18, padding: '12px 0px' }
}

export default App
//export default withAuthenticator(App, {  includeGreetings: true})
